// Filename - Home.js
import React from 'react'
import { Button, Table, Modal } from 'react-bootstrap'
import 'bootstrap/dist/css/bootstrap.min.css';
import array from './array';
import { Link, useNavigate } from 'react-router-dom';

function Home() {

	let history = useNavigate()
	const [show, setShow] = useState(false);
  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

	// You may skip this part if you're
	// using react-context api or redux
	function setId(item) {
		Object.entries(item).forEach(([key, value]) => {
			localStorage.setItem(key, value);
		})
	}

	// Deleted function - functionality
	// for deleting the entry
	function deletedById(id, namaBarang) {
		const itemIndex = array.findIndex(item => item.id === id);
		array.splice(itemIndex, 1);
		handleClose();
		// We need to re-render the page for getting
		// the results so redirect to same page.
		history('/')
	}

	return (
		<div style={{ margin: '10rem' }}>
			<Table striped bordered hover size="sm">
				<thead>
					<tr>
						<th>Nama Barang</th>
						<th>Harga Beli</th>
						<th>Harga Jual</th>	
						<th>Stok</th>
					</tr>
				</thead>
				<tbody>

					{/* Mapping though every element
						in the array and showing the
						data in the form of table */}
					{array.map((item) => {
						return (
							<tr>
								<td>{item.namaBarang}</td>
								<td>{item.hargaBeli}</td>
								<td>{item.hargaJual}</td>
								<td>{item.stok}</td>
								{/* getting theid,name, and
									age for storing these
									value in the jsx with
									onclick event */}
								<td><Link to={`/edit`}>
									<Button onClick={(e) => setId(item)}
									variant="info">
									Edit</Button></Link>
								</td>

								{/* Using thr deleted function passing
									the id of an entry */}
								<td>
									<Button variant="primary" onClick={handleShow}>
										Launch demo modal
									</Button>
									<Button onClick={e => deletedById(item.id, item.namaBarang)}
									variant="danger">Delete</Button></td>
							</tr>
						)
					})}
				</tbody>
			</Table>

      <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>Modal heading</Modal.Title>
        </Modal.Header>
        <Modal.Body>Woohoo, you are reading this text in a modal!</Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
          <Button variant="primary" onClick={deletedById}>
            Save Changes
          </Button>
        </Modal.Footer>
      </Modal>

			{/* Button for redirecting to create page for
				insertion of values */}
			<Link className="d-grid gap-2" to='/create'>
				<Button variant="warning" size="lg">Create</Button>
			</Link>
		</div>
	)
}

export default Home
