// Filename - Home.js
import React, { useState } from 'react';
import { Button, Table, Modal } from 'react-bootstrap'
import 'bootstrap/dist/css/bootstrap.min.css';
import array from './array';
import { Link, useNavigate } from 'react-router-dom';

function Home() {

	let history = useNavigate()
	const [show, setShow] = useState(false);
  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);
	const [id, setId] = useState();
	const [namaBarang, setNamaBarang] = useState();

	// You may skip this part if you're
	// using react-context api or redux
	function saveToLocal(item) {
		Object.entries(item).forEach(([key, value]) => {
			localStorage.setItem(key, value);
		})
	}

	// Deleted function - functionality
	// for deleting the entry
	function doDeleted() {
		const itemIndex = array.findIndex(item => item.id === id);
		array.splice(itemIndex, 1);
		handleClose();
		// We need to re-render the page for getting
		// the results so redirect to same page.
		history('/')
	}

	function deletedById(id, namaBarang) {
		handleShow();
		setId(id);
		setNamaBarang(namaBarang);
	}

	return (
		<div style={{ margin: '10rem' }}>
			<Table striped bordered hover size="sm">
				<thead>
					<tr>
						<th>Image</th>
						<th>Nama Barang</th>
						<th>Harga Beli</th>
						<th>Harga Jual</th>	
						<th>Stok</th>
					</tr>
				</thead>
				<tbody>

					{/* Mapping though every element
						in the array and showing the
						data in the form of table */}
					{array.map((item) => {
						return (
							<tr>
								<td><Image src={item.image} thumbnail /></td>
								<td>{item.namaBarang}</td>
								<td>{item.hargaBeli}</td>
								<td>{item.hargaJual}</td>
								<td>{item.stok}</td>
								{/* getting theid,name, and
									age for storing these
									value in the jsx with
									onclick event */}
								<td><Link to={`/edit`}>
									<Button onClick={(e) => saveToLocal(item)}
									variant="info">
									Edit</Button></Link>
								</td>

								{/* Using thr deleted function passing
									the id of an entry */}
								<td>
									<Button 
										onClick={e => deletedById(item.id, item.namaBarang)} variant="danger">Delete
									</Button>
								</td>
							</tr>
						)
					})}
				</tbody>
			</Table>

      <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>Hapus Barang</Modal.Title>
        </Modal.Header>
        <Modal.Body>Apakah anda akan mengahpus data barang [{namaBarang}]</Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => handleClose()}>
            Tidak
          </Button>
          <Button variant="primary" onClick={() => doDeleted()}>
            Ya
          </Button>
        </Modal.Footer>
      </Modal>

			{/* Button for redirecting to create page for
				insertion of values */}
			<Link className="d-grid gap-2" to='/create'>
				<Button variant="warning" size="lg">Create</Button>
			</Link>
		</div>
	)
}

export default Home
